mapper.select:
select sn, grp_sn, std_sn, user_id, rating, review, to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date
from tgrp_rating
where sn = #{sn}

select sn, grp_sn, std_sn, user_id, rating, review, to_char(reg_date, 'yyyy-mm-dd hh24:mi:ss') as reg_date
from tgrp_rating
where grp_sn = #{grpSn}

mapper.update:
update tgrp_rating
set rating = #{rating},
review = #{review}
where sn = #{sn}

mapper.select:
select round(avg(rating),1)
from tgrp_rating
where grp_sn = #{grp_sn}

select count(*)
from tgrp_rating
where grp_sn = #{grpSn}
