mapper.select:

select sn, brd_sn, user_id, content, cnt, status, reg_date, upd_date
from
    (
        select /*+INDEX(trpl idx_reply) */
            rownum rn, sn, brd_sn, user_id, content, cnt, status, reg_date, upd_date
        from trpl
        where brd_sn = #{brdSn}
          and sn > 0
          and rownum <= #{cri.pageNum} * #{cri.amount}
    )
where rn > (#{cri.pageNum} -1) * #{cri.amount}
