mapper.select:

select *
from (
         select /*+ index_desc(tstd pk_tstd)*/
             rownum rn, s.sn, s.grp_sn, u.id as representation, u.name as representation_name, s.name, s.start_date, s.end_date, s.start_time, s.end_time, s.repeat_cycle, s.repeat_day, s.information, s.on_off, s.on_url, s.place_id, s.expense, s.capacity, s.participants, s.status, s.reg_date, s.upd_date
         from tstd s, tuser u
         where s.representation = u.id and s.status in ('STST01', 'STST03') and rownum <= #{pageNum} * #{amount}
     )
where rn > (#{pageNum} - 1) * #{amount}

select s.sn, s.grp_sn, u.id as representation, u.name as representation_name, s.name, s.start_date, s.end_date, s.start_time, s.end_time, s.repeat_cycle, s.repeat_day, s.information, s.on_off, s.on_url, s.place_id, s.expense, s.capacity, s.participants, s.status, s.reg_date, s.upd_date
from tstd s, tuser u
where s.representation = u.id and s.status in ('STST01', 'STST03') and s.sn = #{sn}


select *
from (
         select rownum rn, sn, grp_sn, name, start_date, end_date, start_time, end_time, repeat_cycle, repeat_day, information, on_off, on_url, place_id, expense, capacity, participants, status, reg_date, upd_date
         from (
                  select s.sn, s.grp_sn, s.name, s.start_date, s.end_date, s.start_time, s.end_time, s.repeat_cycle, s.repeat_day, s.information, s.on_off, s.on_url, s.place_id, s.expense, s.capacity, s.participants, s.status, s.reg_date, s.upd_date
                       ,w.reg_date wish_reg
                  from tstd s, tstd_wish w
                  where s.sn = w.std_sn and s.status in ('STST01', 'STST03') and w.user_id = #{userId} --'jiho@naver.com'
                  order by w.reg_date desc
              )
         where rownum <= #{pageNum} * #{amount}
     )
where rn > (#{pageNum} - 1) * #{amount}
